import { Component, HostListener, OnInit } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { CartService } from '../services/cart.service';
import { Product } from '../products';
// import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent implements OnInit {
  productQuantity = new Map();
  cartProducts: Product[] = [];
  cartProductsPrice = 0
  shippingPrice = 0
  orderTotalPrice = 0
  checkoutForm = this.formBuilder.group({
    name: '',
    address: ''
  });

  constructor(
    private cartService: CartService,
    private formBuilder: FormBuilder,
  ) { }

  ngOnInit(): void {
    this.cartService.retrieve();
    this.updateCart();
  }

  updateCart(){
    this.productQuantity = this.cartService.productQuantity
    this.cartProducts = this.cartService.cartProducts
    this.cartProductsPrice = this.cartService.cartProductsPrice
  }

  getOrderTotalPrice(): number {
    this.cartProductsPrice = this.cartService.getCartProdcutsPrice();
    this.shippingPrice = this.cartService.getShippingPrice();
    return this.orderTotalPrice = this.cartProductsPrice + this.shippingPrice;
  }

  onSubmit(): void {
    const productQuantityArray = Array.from(this.productQuantity, ([id, quantity]) => ({ id, quantity }));
    const orderDetails = {
      "orderDetails": {
        "price": this.orderTotalPrice,
        "name": this.checkoutForm.value.name,
        "address": this.checkoutForm.value.address,
        "productsData": productQuantityArray
      }
    }
    this.cartService.createOrder(orderDetails).subscribe(
      (response) => {
        console.log(response);
        window.alert('Successful Operation');
        this.checkoutForm.reset();
        this.cartService.clearCart();
        this.updateCart();
      },
      (error) => {
        console.error('Internal Server Error: Order Not Created', error);
        window.alert('Something went wrong please try again');
      }
    );
  }

  onDelteCartProduct(productId: number): void{
    const product = this.cartProducts.find(product => product.id === productId);
    this.cartService.deleteFromCart(product!);
    this.updateCart();
  }
}
<h3>Cart</h3>

<!-- <p>
  <a routerLink="/shipping">Shipping Prices</a>
</p> -->

<div *ngIf="cartProducts && cartProducts.length > 0; else noCartProducts">
  <div class="box">
    <div class="cart-item header">
      <span>Product</span>
      <span>Unit Price</span>
      <span>Quantity</span>
      <span>Total Price</span>
      <span>Action</span>
    </div>

    <div class="cart-item" *ngFor="let product of cartProducts">
      <span>{{ product.name }}</span>
      <span>{{ product.price | currency:'EUR' }}</span>
      <input type="number" [(ngModel)]="productQuantity[product.id]" />
      <span>{{ product.price * this.productQuantity.get(product.id)! | currency:'EUR' }}</span>
      <span><button class="button" type="button" (click)="onDelteCartProduct(product.id)">Delete</button></span>
    </div>
  </div>

  <div class="box">
    <div class="price-item">
      <span>Items: </span>
      <span>{{cartProductsPrice | currency:'EUR'}}</span>
    </div>

    <div class="price-item">
      <span> Shipping: </span>
      <span> {{shippingPrice | currency:'EUR'}}</span>
    </div>

    <div class="total-price-item">
      <span> Order Total: </span>
      <span>{{ getOrderTotalPrice() | currency:'EUR' }}</span>
    </div>
  </div>

  <form [formGroup]="checkoutForm" (ngSubmit)="onSubmit()">
    <div>
      <label for="name">
        Name
      </label>
      <input id="name" type="text" formControlName="name">
    </div>

    <div>
      <label for="address">
        Address
      </label>
      <input id="address" type="text" formControlName="address">
    </div>
    <button class="button" type="submit">Purchase</button>
  </form>
</div>


<ng-template #noCartProducts>
  <div>
    <p>No items in the cart yet.</p>
  </div>
</ng-template>
