import { Component } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { CartService } from '../services/cart.service';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent {
  currrency = "TND"
  items = this.cartService.getItems();
  itemsPrice = 0
  shippingPrice = 0
  orderTotalPrice = 0
  checkoutForm = this.formBuilder.group({
    name: '',
    address: ''
  });

  constructor(
    private cartService: CartService,
    private formBuilder: FormBuilder,
  ) { }

  getItemsPrice(): void {
    this.itemsPrice = 0;
    this.items.forEach((item) => {
      this.itemsPrice += item.price;
    });
  }

  getShippingPrice(): void {
    this.shippingPrice = 10
  }

  getOrderTotalPrice(): number {
    this.getItemsPrice();
    this.getShippingPrice();
    return this.orderTotalPrice = this.itemsPrice + this.shippingPrice;
  }

  onSubmit(): void {
    const products = this.cartService.clearCart();
    window.alert('Your order has been submitted');
    console.warn('Your order has been submitted', this.checkoutForm.value);
    this.checkoutForm.reset();

    this.cartService.createOrder(products).subscribe(
      (response) => {
        console.log('Order created successfully', response);
      },
      (error) => {
        console.error('Internal Server Error: Order Not Created', error);
      }
    );
  }

  // Fix the refresh page items disappear.

}
