import { Component } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { CartService } from '../services/cart.service';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent {
  currrency = "TND"
  items = this.cartService.getItems();
  itemsPrice = 0
  shippingPrice = 0
  orderTotalPrice = 0
  checkoutForm = this.formBuilder.group({
    name: '',
    address: ''
  });

  constructor(
    private cartService: CartService,
    private formBuilder: FormBuilder,
  ) { }

  getItemsPrice(): void {
    this.itemsPrice = 0;
    this.items.forEach((item) => {
      this.itemsPrice += item.price;
    });
  }

  getShippingPrice(): void {
    this.shippingPrice = 10
  }

  getOrderTotalPrice(): number {
    this.getItemsPrice();
    this.getShippingPrice();
    return this.orderTotalPrice = this.itemsPrice + this.shippingPrice;
  }

  onSubmit(): void {
    const products = this.cartService.clearCart();
    window.alert('Your order has been submitted');
    console.warn('Your order has been submitted', this.checkoutForm.value);
    this.checkoutForm.reset();

    this.cartService.createOrder(products).subscribe(
      (response) => {
        console.log('Order created successfully', response);
      },
      (error) => {
        console.error('Internal Server Error: Order Not Created', error);
      }
    );
  }

  // Fix the refresh page items disappear.

}
<h3>Cart</h3>

<!-- <p>
  <a routerLink="/shipping">Shipping Prices</a>
</p> -->

<div *ngIf="items && items.length > 0; else noItems">
  <div class="box">
    <div class="cart-item" *ngFor="let item of items">
      <span>{{ item.name }}</span>
      <span>{{ item.price | currency:'EUR' }}</span>
    </div>
  </div>
  <form [formGroup]="checkoutForm" (ngSubmit)="onSubmit()">

    <div>
      <label for="name">
        Name
      </label>
      <input id="name" type="text" formControlName="name">
    </div>

    <div>
      <label for="address">
        Address
      </label>
      <input id="address" type="text" formControlName="address">
    </div>
    <div class="box">
      <div class="price-item">
        <span>Items: </span>
        <span>{{itemsPrice | currency:'EUR'}}</span>
      </div>

      <div class="price-item">
        <span> Shipping: </span>
        <span> {{shippingPrice | currency:'EUR'}}</span>
      </div>

      <div class="total-price-item">
        <span> Order Total: </span>
        <span>{{ getOrderTotalPrice() | currency:'EUR' }}</span>
      </div>
    </div>

    <button class="button" type="submit">Purchase</button>

  </form>
</div>

<ng-template #noItems>
  <div >
    <p>No items in the cart yet.</p>
  </div>
</ng-template>
