import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { CartService } from '../services/cart.service';
import { ProductDetailService } from '../services/product-detail.service';

@Component({
  selector: 'app-product-detail',
  templateUrl: './product-detail.component.html',
  styleUrls: ['./product-detail.component.css']
})
export class ProductDetailComponent {
  product: any;
  quantity: number = 1;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private cartService: CartService,
    private productDetailService: ProductDetailService
  ) { }

  ngOnInit() {
    this.route.params.subscribe(params => {
      this.productDetailService.getProductDetails(params['productId']).subscribe((data) => {
        this.product = data;
      });
    })
  }

  addToCart() {
    this.cartService.updateItem(this.product!, this.quantity);
    const confirmationMessage = `Add ${this.quantity} ${this.product?.name}(s) to the cart?\n\nSelect "OK" to go to the checkout page or "Cancel" to continue shopping.`;
    const isConfirmed = window.confirm(confirmationMessage);
    if (isConfirmed) {
      this.router.navigate(['/cart']);
  }
}
